export function uploadFiles(url, filePathArr) {
  return uploadFilesSync(url, filePathArr, 0, {
    success: [],
    fail: []
  });
}

function uploadFilesSync(url, filePathArr, index, result) {
  return upload(url, filePathArr[index++], {})
    .then(res => {
      result.success.push({
        index: index,
        res: res
      });
      if (index === filePathArr.length) {
        return result;
      } else {
        return uploadFilesSync(url, filePathArr, index, result);
      }
    })
    .catch(err => {
      result.fail.push({
        index: index,
        res: err
      });
      if (index === filePathArr.length) {
        return result;
      } else {
        return uploadFilesSync(url, filePathArr, index, result);
      }
    });
}

export function upload(url, filePath, formData = {}) {
  let header = {
    "Content-Type": "multipart/form-data"
  };
  return new Promise((resolve, reject) => {
    wx.uploadFile({
      url: config.server + url,
      header: header,
      filePath: filePath,
      name: "file",
      formData: formData,
      success(res) {
        resolve(res.data);
      },
      fail: function(error) {
        reject(error);
      }
    });
  });
}
